<script>
import { Fragment } from "vue-fragment";
import {SocialLinks} from '../../data'

export default {
  data: () => {
    return {
      links:SocialLinks,
      color:'text-white'
    };
  },
  components: {
    Fragment,
  },
  methods:{
    change_theme:function(){
      if(!localStorage.getItem('theme')){
        this.color='text-black'
        return this.$store.commit('change_theme','theme-light')
      }
      else{
        if(localStorage.getItem('theme')==='theme-dark'){
          this.color='text-black'
         return this.$store.commit('change_theme','theme-light') 
      }else{
        this.color='text-white'
        return this.$store.commit('change_theme','theme-dark') 
      }
      }
      
    }
  },
  computed:{
    change_btn_color:function(){
      return this.$store.state.colors['color_1']
    }
  }
};
</script>
